import { React, useState } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory,
  useParams,
} from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Overlay from "react-overlays/esm/Overlay";

function ResetPassword() {
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [error, setError] = useState(false);
  const [errMessage, setErrMessage] = useState("");
  const history = useHistory();
  const { token } = useParams();

  async function handleResetPassword(res) {
    //const queryParams = new URLSearchParams(window.location.search);
    //const token = queryParams.get('token');
    console.log(token);
    if (password.length() == 0 && password !== passwordConfirm) {
      setError(true);
      setErrMessage("Passwords don't match");
    } else {
      const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          password: password,
        }),
      };
      const encodedValue = encodeURIComponent(token);
      const response = await fetch(
        "/password/reset/submit?token=" + encodedValue,
        requestOptions
      );

      if (response.ok) {
        history.push("/");
      } else {
        const x = await response.json();
        console.log(x);
        setError(true);
        setErrMessage(x.message);
      }
    }
  }

  function displayError() {
    if (error) {
      return (
        <div>
          <Overlay show={error} placement="right">
            {({ placement, arrowProps, show: _show, popper, ...props }) => (
              <div
                {...props}
                style={{
                  backgroundColor: "rgba(255, 100, 100, 0.85)",
                  padding: "2px 10px",
                  color: "white",
                  borderRadius: 3,
                  ...props.style,
                }}
              >
                {errMessage}
              </div>
            )}
          </Overlay>
        </div>
      );
    }
  }

  return (
    <div className="ResetPassword">
      <h1>Forgot Password</h1>
      <Form>
        <Form.Group size="lg" controlId="password">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            autoFocus
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Form.Label>Confirm New Password</Form.Label>
          <Form.Control
            autoFocus
            type="password"
            value={passwordConfirm}
            onChange={(e) => setPasswordConfirm(e.target.value)}
          />
        </Form.Group>
        <Button block size="lg" type="submit" onClick={handleResetPassword}>
          Reset Password
        </Button>
      </Form>
      {/* {displayError()} */}
    </div>
  );
}

export default ResetPassword;
